mapper.select:
SELECT *
FROM cart
WHERE user_id = #{user_id} AND product_id = #{product_id}

mapper.update:
UPDATE cart
SET quantity = #{quantity}
WHERE user_id = #{user_id} AND product_id = #{product_id}

mapper.delete:
DELETE FROM cart
WHERE user_id = #{user_id}
AND product_id IN

    #{product_id}
