mapper.select:
SELECT COUNT(*)
FROM PRODUCT
WHERE area = #{area}

	AND category IN

		#{category}

SELECT p.product_id, p.product_name, p.brand_name, p.price, p.stock, (SELECT img_url FROM PRODUCT_IMG i WHERE i.product_id = p.product_id LIMIT 1) AS img_url, IFNULL(pc.click_count, 0) AS click_count FROM PRODUCT p LEFT JOIN ( SELECT product_id, COUNT(product_click_id) AS click_count FROM PRODUCT_CLICK GROUP BY product_id ) pc ON p.product_id = pc.product_id WHERE p.area = #{area}
AND p.category IN
#{category}
ORDER BY p.product_id DESC
ORDER BY p.price ASC
ORDER BY p.price DESC
ORDER BY click_count DESC
LIMIT #{limit} OFFSET #{offset}
